sys_variable_value.value:
(function execute(inputs, outputs) {
  var parentContextGr = new GlideRecord('sys_flow_context');
  parentContextGr.addQuery('source_table',inputs.source_table.toString());
  parentContextGr.addQuery('source_record',inputs.source_record.toString());
  parentContextGr.query();
  if(parentContextGr.next()){
    var childContextGr = new GlideRecord('sys_flow_context');
    childContextGr.addQuery('source_table','sys_flow_context');
    childContextGr.addQuery('source_record',parentContextGr.sys_id.toString());
    childContextGr.query();
    if(childContextGr.next()){
      sn_fd.FlowAPI.nudgeFlow(childContextGr.sys_id.toString(), 1);
    }
  }

})(inputs, outputs);

