 "Name": "Submit",
 "Target_HTTP_Post_or_URL_": httpPostUrl,
 "Fields_Action_Card_": [
 ]
 }
 ]
 }
 ];

 inputs.recipient_user_record = recipient;
 inputs.rest_api_body = '\\"sys_id\\":\\"' + restBody +'\\"';
 inputs.header_text_under_logo_ = header;
 inputs.subheader_text_under_header_text_ = subHeader;

 var result = sn_fd.FlowAPI.getRunner().subflow('x_apig_adaptive_ca.generate_adaptive_card_form').inForeground().withInputs(inputs).run();
 var outputs = result.getOutputs();
 gs.info(originalJSON);
 var originalJSON = JSON.parse(outputs.output_json);

 var outputs = {};
 var ritmUtils = new global.RequestedItemUtils();
 var variableList = ritmUtils.getVariables(ritmGr);
 outputs.variableList = variableList;
 var choiceList = [];
 for (var i = 0; i < variableList.length; i++) {
 choiceList.push({ "title": variableList[i].title, "value": variableList[i].value });
 }

 var detailsCard = {
 "type": "Action.ShowCard",
 "name": "View Details",
 "title": "View Details",
 "card": {
 "type": "AdaptiveCard",
 "style": "emphasis",
 "body": [
 {
 "type": "TextBlock",
 "id": "Requested_Items",
 "text": "**Requested Item(s)**",
 "wrap": true
 },
 {
 "type": "FactSet",
 "id": "RITM_Facts",
 "facts": choiceList
 }
 ]
 }
 };

 for (var i = 0; i < originalJSON.body[1].items[2].items.length; i++) {
 if (originalJSON.body[1].items[2].items[i].type == "ActionSet") {
 originalJSON.body[1].items[2].items[i].actions.push(detailsCard);
 break;
 }
 }
 return JSON.stringify(originalJSON);
}
catch (e) {
 gs.error("Error in 'Ask for task/actionable approval' flow: " + e.toString());
 return '';
}"}}

